mapper.select:
select
    detect_staff_info.birthday,
    detect_staff_info.contact,
    detect_staff_info.create_time,
    detect_staff_info.edu_degree,
    detect_staff_info.gender,
    detect_staff_info.grade_gain_date,
    detect_staff_info.grade_id,
    detect_staff_info.id,
    detect_staff_info.id_card_no,
    detect_staff_info.is_delete,
    detect_staff_info.name,
    detect_staff_info.operator,
    detect_staff_info.position_id,
    detect_staff_info.qualification_audit_date,
    detect_staff_info.qualification_gain_date,
    detect_staff_info.qualification_grade,
    detect_staff_info.qualification_invalid_date,
    detect_staff_info.qualification_valid_time,
    detect_staff_info.remark,
    detect_staff_info.status,
    detect_staff_info.tec_category,
    detect_staff_info.update_time,
    detect_staff_info.workarea_id
form detect_staff_info


    detect_staff_info.birthday = #{birthday}


    and detect_staff_info.contact = #{contact}


    and detect_staff_info.create_time = #{createTime}


    and detect_staff_info.edu_degree = #{eduDegree}


    and detect_staff_info.gender = #{gender}


    and detect_staff_info.grade_gain_date = #{gradeGainDate}


    and detect_staff_info.grade_id = #{gradeId}


    and detect_staff_info.id_card_no = #{idCardNo}


    and detect_staff_info.is_delete = #{isDelete}


    and detect_staff_info.name = #{name}


    and detect_staff_info.operator = #{operator}


    and detect_staff_info.position_id = #{positionId}


    and detect_staff_info.qualification_audit_date = #{qualificationAuditDate}


    and detect_staff_info.qualification_gain_date = #{qualificationGainDate}


    and detect_staff_info.qualification_grade = #{qualificationGrade}


    and detect_staff_info.qualification_invalid_date = #{qualificationInvalidDate}


    and detect_staff_info.qualification_valid_time = #{qualificationValidTime}


    and detect_staff_info.remark = #{remark}


    and detect_staff_info.status = #{status}


    and detect_staff_info.tec_category = #{tecCategory}


    and detect_staff_info.update_time = #{updateTime}


    and detect_staff_info.workarea_id = #{workareaId}
