mapper.update:
      update device_damage_map


            device_damage_map.damage_id = #{damageId},


            device_damage_map.deivce_id = #{deivceId},


            device_damage_map.device_type = #{deviceType}


where
        device_damage_map.id = #{id}

mapper.select:
select
    device_damage_map.damage_id,
    device_damage_map.deivce_id,
    device_damage_map.device_type,
    device_damage_map.id
form device_damage_map


    device_damage_map.damage_id = #{damageId}


    and device_damage_map.deivce_id = #{deivceId}


    and device_damage_map.device_type = #{deviceType}
