mapper.select:
select
    device_position.create_time,
    device_position.device_code,
    device_position.device_no,
    device_position.direction,
    device_position.id,
    device_position.line_category,
    device_position.line_id,
    device_position.line_name,
    device_position.miles,
    device_position.station_id,
    device_position.stock_exchange,
    device_position.thigh_track_id,
    device_position.track_id,
    device_position.update_time
form device_position


    device_position.create_time = #{createTime}


    and device_position.device_code = #{deviceCode}


    and device_position.device_no = #{deviceNo}


    and device_position.direction = #{direction}


    and device_position.line_category = #{lineCategory}


    and device_position.line_id = #{lineId}


    and device_position.line_name = #{lineName}


    and device_position.miles = #{miles}


    and device_position.station_id = #{stationId}


    and device_position.stock_exchange = #{stockExchange}


    and device_position.thigh_track_id = #{thighTrackId}


    and device_position.track_id = #{trackId}


    and device_position.update_time = #{updateTime}
