mapper.select:
select
    station_line.cent_distance,
    station_line.create_time,
    station_line.direction,
    station_line.end_distance,
    station_line.id,
    station_line.is_delete,
    station_line.line_id,
    station_line.operator,
    station_line.sort_num,
    station_line.start_distance,
    station_line.station_id,
    station_line.status,
    station_line.update_time
form station_line


    station_line.cent_distance = #{centDistance}


    and station_line.create_time = #{createTime}


    and station_line.direction = #{direction}


    and station_line.end_distance = #{endDistance}


    and station_line.is_delete = #{isDelete}


    and station_line.line_id = #{lineId}


    and station_line.operator = #{operator}


    and station_line.sort_num = #{sortNum}


    and station_line.start_distance = #{startDistance}


    and station_line.station_id = #{stationId}


    and station_line.status = #{status}


    and station_line.update_time = #{updateTime}
