mapper.select:
select
    sys_system_dictionary.dicid,
    sys_system_dictionary.enum_name,
    sys_system_dictionary.enum_type,
    sys_system_dictionary.sort_id,
    sys_system_dictionary.status,
    sys_system_dictionary.system_id,
    sys_system_dictionary.text,
    sys_system_dictionary.value
form sys_system_dictionary


    sys_system_dictionary.enum_name = #{enumName}


    and sys_system_dictionary.enum_type = #{enumType}


    and sys_system_dictionary.sort_id = #{sortId}


    and sys_system_dictionary.status = #{status}


    and sys_system_dictionary.system_id = #{systemId}


    and sys_system_dictionary.text = #{text}


    and sys_system_dictionary.value = #{value}
