mapper.select:
select
    workarea_info.create_time,
    workarea_info.full_name,
    workarea_info.id,
    workarea_info.input_code,
    workarea_info.is_delete,
    workarea_info.operator,
    workarea_info.short_name,
    workarea_info.update_time,
    workarea_info.workshop_id
form workarea_info


    workarea_info.create_time = #{createTime}


    and workarea_info.full_name = #{fullName}


    and workarea_info.input_code = #{inputCode}


    and workarea_info.is_delete = #{isDelete}


    and workarea_info.operator = #{operator}


    and workarea_info.short_name = #{shortName}


    and workarea_info.update_time = #{updateTime}


    and workarea_info.workshop_id = #{workshopId}
