mapper.select:
select
    workshop_info.create_time,
    workshop_info.full_name,
    workshop_info.id,
    workshop_info.input_code,
    workshop_info.is_delete,
    workshop_info.line_id,
    workshop_info.operator,
    workshop_info.short_name,
    workshop_info.track_division_id,
    workshop_info.update_time,
    workshop_info.workshop_type
form workshop_info


    workshop_info.create_time = #{createTime}


    and workshop_info.full_name = #{fullName}


    and workshop_info.input_code = #{inputCode}


    and workshop_info.is_delete = #{isDelete}


    and workshop_info.line_id = #{lineId}


    and workshop_info.operator = #{operator}


    and workshop_info.short_name = #{shortName}


    and workshop_info.track_division_id = #{trackDivisionId}


    and workshop_info.update_time = #{updateTime}


    and workshop_info.workshop_type = #{workshopType}
