select:
select
t1.*
from contract_sign t1
-- inner join system_dictionary t2 on t1.verify_state=t2.value and t2.type='verify_state'


       mobile=#{mobile}


       and email=#{email}


       and compony=#{compony}


       and real_name=#{realName}


       and sign_status=#{signStatus}


       and need_pay=#{needPay}


       and pay_status=#{payStatus}

    and create_time>#{createTime}
    and create_time <#{createTime}

order by t1.create_time desc
